from __future__ import annotations

import argparse
from pathlib import Path

from all_repos import autofix_lib
from all_repos.grep import repos_matching

# Find repos that have this file...
FILE_NAME = "README.md"
# ... and which content contains this string.
FILE_CONTAINS = "https://img.shields.io/badge/packaging-poetry-299bd7?style=flat-square&logo=data:image/png;base64,"
# Git stuff
GIT_COMMIT_MSG = "chore: switch to official Poetry badge"
GIT_BRANCH_NAME = "chore/poetry-badge"


def apply_fix():
    """Apply fix to a matching repo."""
    file_paths = [
        Path(FILE_NAME),
        Path("project") / f"{FILE_NAME}.jinja",
    ]
    new_url = "https://img.shields.io/endpoint?url=https://python-poetry.org/badge/v0.json"
    for readme_md in file_paths:
        if not readme_md.exists():
            continue

        file_content = readme_md.read_text()
        if new_url in file_content:
            continue

        file_content = file_content.replace(
            FILE_CONTAINS,
            new_url,
        )
        readme_md.write_text(file_content)


# You shouldn't need to change anything below this line


def find_repos(config) -> set[str]:
    """Find matching repos using git grep."""
    repos = repos_matching(
        config,
        (FILE_CONTAINS, "--", FILE_NAME),
    )
    return repos


def main():
    """Entry point."""
    parser = argparse.ArgumentParser()
    autofix_lib.add_fixer_args(parser)
    args = parser.parse_args(None)

    repos, cfg, commit, stg = autofix_lib.from_cli(
        args,
        find_repos=find_repos,
        msg=GIT_COMMIT_MSG,
        branch_name=GIT_BRANCH_NAME,
    )
    autofix_lib.fix(
        repos,
        apply_fix=apply_fix,
        config=cfg,
        commit=commit,
        autofix_settings=stg,
    )
    return 0


if __name__ == "__main__":
    raise SystemExit(main())
